import React, { useState,useEffect } from 'react'
import $ from 'jquery';
import {
  CButton,
  CCard,
  CCardBody,
  CCardHeader,
  CCol,
  CContainer,
  CJumbotron,
  CRow,
  CEmbed,
  CDropdown,
  CDropdownToggle,
  CDropdownDivider,
  CDropdownHeader,
  CModal,
  CModalBody,
  CModalFooter,
  CModalHeader,
  CModalTitle,
  CDropdownItem,
  CDropdownMenu,
  CHeaderNavLink,
  CEmbedItem
} from '@coreui/react'
import { DocsLink } from 'src/reusable'
import Cookies from 'universal-cookie';
import BASE_URL from 'src/views/base';


const cookies = new Cookies();
console.log(cookies.get('gym_uuid'))


const Classes = () => {

const [Policy, setPolicy] = useState([])


useEffect(() => {
axios
    .get(BASE_URL + 'gymprofile/location/?gym=' + cookies.get('gym_uuid'))
    .then(res => {
    console.log(res)
    setPolicy(res.data)
    })
    .catch(err => {
    console.log(err)
    })
}, [])

const axios = require('axios');

const [policydetail, setpolicydetail] = useState(["ffgyu","fghj"])

function Clicker(uuid) {
    // alert(uuid);
    setSuccess(!success);
    //  debugger
    axios
      .get(BASE_URL + 'gymprofile/location/' + uuid)
      .then(getuuid => {
        console.log(getuuid);
        setpolicydetail(getuuid.data);
      })

  }




async function NewPolicy() {
    // setSuccess(false);

    //  debugger
    // var form = document.forms["regform"]["email"].value;   
    var location = document.getElementById("location").value;
    // var policy_text = document.getElementById("policy_text").value;

    {  

        console.log('Trying to send request');

        try {
            console.log('Trying');
            let res = await axios.post(BASE_URL + 'gymprofile/location/', {
                gym_location:location,
                // policy_text:policy_text,
                gym: cookies.get('gym_uuid'),
            });
            if (res.status === 200) {
                //  debugger
                // test for status you want, etc
                console.log(res.status);
                console.log(res.data);
                // alert(res.data.message);

                window.location.reload();


            }
            //  debugger
            // Don't forget to return something
            return res.data
        }
        catch (err) {
            console.error('Signup Failed , Please try again.');
            window.location.reload()
            alert(err);
        }
    }
