                <CHeaderNavLink>
                <button className="mn_btn_1 mr-1" onClick={() => setPrimary(!primary)}> <i class="fa fa-plus-circle" aria-hidden="true"></i> Add New Ammenity </button>
                </CHeaderNavLink>


                </div> 
